package actividades;

public class Act10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub //EJERCICIOS 10,11 y 12.

		for (int i = 100; i >= 0; i--) {
			System.out.println(i);
		}
		
		int i = 100;
		while (i  >= 0) {
			System.out.println(i);
			i--;
		}
		int i1 = 100;
		do {
			System.out.println(i1);
			i1 --;
		}while (i1 >= 0);
		
	}

}
