package actividades;

public class Act16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub //EJERCICIOS 16,17,18

		
		for (int a = 320; a >= 160 ; a = a - 20) {
			System.out.println(a);
		}
		
		int b = 320;
		while (b >= 160) {
			System.out.println(b);
			b = b - 20;
		}
		
		int c = 320;
		do {
			System.out.println(c);
			c = c -20;
		}while(c >= 160);
		
		
	}

}
